public class javaVar2 {
    
    public static void main(String[] args) {

        int x = 10;
        double d = x;
        long l = x;
        float f = x;
        short s = 20;
        x = s;

        double valorDouble = 9.99;
        int valorInt = (int) valorDouble; //Entre o () coloca o tipo de dados que estou forçando a fazer na conversão
        System.out.println(valorInt);
    }
}

